import 'package:hive_flutter/hive_flutter.dart';
import '../modules/user.dart';
import 'dart:io';
import 'package:image/image.dart' as img;
import 'dart:typed_data';
import 'dart:convert';

class UserUtils {
  final userBox = Hive.box("user_box");

  String profileImage = "";

  User user = User(id: 0, name: "", profileImage: "");

  static Future<String> imageToBase64(String imagePath) async {
    final bytes = await File(imagePath).readAsBytes();
    final base64String = base64Encode(bytes);
    return base64String;
  }

  static Uint8List imageToUint8List(img.Image image) {
    final img.Image imgFormat = img.copyResize(image, width: 800, height: 800);
    final Uint8List uint8List = Uint8List.fromList(img.encodePng(imgFormat));
    return uint8List;
  }

  static Future<void> updateUser(int itemKey, User item) async {
    final userBox = Hive.box("user_box");
    await userBox.put(itemKey, item);
  }

  static Uint8List base64ToUint8List(String base64String) {
    final bytes = base64Decode(base64String);
    return Uint8List.fromList(bytes);
  }

  static img.Image base64ToImage(String base64String) {
    final Uint8List uint8List = base64ToUint8List(base64String);
    final img.Image image = img.decodeImage(uint8List)!;
    return image;
  }

  static String unknownImage() {
    return "";
  }
}
